    // 업체 환경 정보
    $cust_ip = getenv( "REMOTE_ADDR" );

    /* = -------------------------------------------------------------------------- = */
    /* =   03-1. 취소 요청                                                     = */
    /* = -------------------------------------------------------------------------- = */

        $mod_type = $_POST[ "mod_type" ];

        $tx_cd = "00200000";

        $c_PayPlus->mf_set_modx_data( "tno"     , $_POST[ "tno"       ] );      // NHNKCP 원거래 거래번호
