    /* ============================================================================== */


    /* ============================================================================== */
    /* =   03-2. 실행                                                               = */
    /* ------------------------------------------------------------------------------ */

        $c_PayPlus->mf_do_tx( "", $g_conf_home_dir, $g_conf_site_cd, $g_conf_site_key, $tx_cd, "",
